eady a video with the same ID for the same query, process further                                                                                              				if ($num_rows == 0)					  				{					$feedURL = "http://gdata.youtube.com/feeds/api/videos/$ytID";//echo($feedURL."\n");sleep(1);					if(file_get_contents($feedURL))					{						$entry = simplexml_load_file($feedURL);//Convert the well-formed XML document in the given file to an object. 						$video = parseVideoEntry($entry);												$timestamp = time();						$title = $video->title;						$description = $video->description;						$username = $video->username;						$upload_time = $video->published;						$duration = $video->length;						$category = $video->category;						$video_url = $video->watchURL;						$thumb_url = 'http://i1.ytimg.com/vi/' . $ytID . '/0.jpg';//echo($thumb_url. "\n");						$keywords = $video->keywords;						$view_count = $video->viewCount;						$rating_count = $video->numrating;						$rating_avg = $video->rating;						$comment_count = $video->commentsCount;						$favorited = $video->favoriteCount;							$response_count = $video->responseCount;																		$query = "INSERT INTO $oTableName VALUES('','$qid','$ytID','$timestamp','','','$today'";//echo($oTableName);						$dquery = "SHOW COLUMNS FROM $oTableName";						$result = mysql_query($dquery) or mysql_error();						while($line = mysql_fetch_assoc($result))						{							$fieldName = $line['Field'];							switch($fieldName)							{								case 'title':									$query = $query . ",'$title'";									break;								case 'description':									$query = $query . ",'$description'";									break;								case 'username':									$query = $query . ",'$username'";									break;								case 'upload_time':									$query = $query . ",'$upload_time'";									break;								case 'duration':									$query = $query . ",'$duration'";									break;								case 'category':									$query = $query . ",'$category'";									break;								case 'keywords':									$query = $query . ",'$keywords'";									break;								case 'video_url':									$query = $query . ",'$video_url'";									break;								case 'thumb_url':									$query = $query . ",'$thumb_url'";									break;								case 'view_count':									$query = $query . ",'$view_count'";									break;								case 'rating_count':									$query = $query . ",'$rating_count'";									break;								case 'rating_avg':									$query = $query . ",'$rating_avg'";									break;								case 'comment_count':									$query = $query . ",'$comment_count'";									break;								case 'comments':									$query = $query . ",'$comments'";									break;								case 'response_count':									$query = $query . ",'$response_count'";									break;								case 'favorite_count':									$query = $query . ",'$favorited'";									break;								case 'rank':									$query = $query . ",'$rank'";									break;											default:									break;										} // switch($fieldName)						} // while($line = mysql_fetch_assoc($result))												$query = $query . ")";echo($query."\n");						$result = mysql_query($query) or mysql_error();//echo($result."d\n");					}				} // if ($num_rows == 0)						fwrite($fr, "$rank $ytID\n");				$rank++;			} // foreach ($rss->items as $item) 		} // for ($index=1; $index<=51; $index+=50)		fclose($fr);	}		 ?>